import React, { useState, useEffect } from "react";
import NewExercise from "./Searchable";
import exerciseList from "./Exercise";
import { useOutletContext } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function NewExercisePage(props) {
    const { value, setValue, addNewExerciseCard } = useOutletContext();
    const navigate = useNavigate();
    const [response, setResponse] = useState(null);
    const [loading, setLoading] = useState(true); // Add loading state

    useEffect(() => {
        axios.get("http://localhost:5000/")
            .then(res => {
                setResponse(res.data); // Set the data in response
                setLoading(false); // Set loading to false after data is fetched
            })
            .catch(err => {
                console.error("Error fetching data:", err);
                setLoading(false); // Set loading to false if there's an error
            });
    }, []);

    if (loading) {
        return <div>Loading...</div>; // Show loading text or spinner while data is being fetched
    }

    return (
        <div>
            <button onClick={() => { navigate(-1); }}>
                Go back
            </button>
            <NewExercise
                options={response || []} // Default to empty array if response is null
                label="name"
                id="id"
                selectedVal={value}
                handleChange={(val) => setValue(val)}
                addNewExercise={addNewExerciseCard}
            />
        </div>
    );
}

export default NewExercisePage;
