import React, { useState } from "react";
import styles from "./App.module.css";
import ExerciseCard from "./CurrentWorkout/Exercisecard";
import NewExercise from "./CurrentWorkout/Searchable";
import exerciseList from "./CurrentWorkout/Exercise";
import { Outlet } from "react-router-dom";

function App() {
    const [currExercises, handleExercises] = useState({ "Chest Press": [ {sno: "1", reps: "10", weight: "10"} ] });
    const [value, setValue] = useState("");
    function addNewSet(newSetDetails, exerciseName) {
        handleExercises((prev) => {
            return {
                ...prev,
                [exerciseName]: [
                    ...(prev[exerciseName]),
                    {
                        sno: prev[exerciseName].length+ 1,
                        reps: newSetDetails.reps,
                        weight: newSetDetails.weight
                    }
                ]
            };
        }
        )
    };
    function addNewExerciseCard(exerciseName) {
        console.log(exerciseName);
        handleExercises({...currExercises,[exerciseName]:[]});
    }
    return (
        <BrowserRouter>
        <Routes>
            <Route index element={<Home />} />
            <Route path="/newSession" element={<App />}>
                <Route path="currentWorkout" element={<CurrentWorkoutPage/>}/>
                <Route path="newExercise" element={<NewExercisePage />} />
            </Route>
        </Routes>
    </BrowserRouter>
    );
}

export default App;
