import React, { useState, useEffect } from "react";
import styles from "./Searchable.module.css";

function NewExercise(props) {
    const [query, setQuery] = useState("");
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        const handleEscape = (e) => {
            if (e.key === "Escape") setIsOpen(false);
        };
        document.addEventListener("keydown", handleEscape);
        return () => document.removeEventListener("keydown", handleEscape);
    }, []);

    const getDisplayValue = () => {
        if (query) return query;
        return props.selectedVal || "Select an option...";
    };

    const filter = (options) => {
        return options.filter((option) =>
            option[props.label].toLowerCase().includes(query.toLowerCase())
        );
    };

    const selectOption = (option) => {
        setQuery("");
        props.handleChange(option[props.label]);
        props.addNewExercise(option[props.label]); // Add the selected exercise
        setIsOpen(false);
    };

    return (
        <div>
            <button
                className={styles.openPopupButton}
                onClick={() => setIsOpen(true)}
            >
                {getDisplayValue()}
            </button>

            {isOpen && (
                <div className={`${styles["popup-overlay"]} ${isOpen ? styles.open : ""}`}>
                    <div className={styles.popup}>
                        <button
                            className={styles["close-btn"]}
                            onClick={() => setIsOpen(false)}
                        >
                            &times;
                        </button>
                        <input
                            type="text"
                            value={query}
                            placeholder="Search..."
                            onChange={(e) => setQuery(e.target.value)}
                        />
                        <div className={styles.options}>
                            {filter(props.options).map((option, index) => (
                                <div
                                    key={`${props.id}-${index}`}
                                    className={styles.option}
                                    onClick={() => selectOption(option)}
                                >
                                    {option[props.label]}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default NewExercise;
