import React, { useState } from "react";
import styles from "./App.module.css";
import ExerciseCard from "./Exercisecard";
import NewExercise from "./Searchable";
import exercise from "./Exercise";

function App() {
    const [currExercise, setCurrExercise] = useState([]);
    const [value, setValue] = useState("");

    function addNewExerciseCard(exerciseName) {
        setCurrExercise([exerciseName, ...currExercise]);
    }

    return (
        <div>
            <NewExercise
                options={exercise}
                label="name"
                id="id"
                selectedVal={value}
                handleChange={(val) => setValue(val)}
                addNewExercise={addNewExerciseCard}
            />
            {currExercise.length === 0 ? (
                <p>No exercises added yet. Select from the dropdown above.</p>
            ) : (
                currExercise.map((e, index) => (
                    <ExerciseCard key={index} exerciseName={e} />
                ))
            )}
        </div>
    );
}

export default App;
