import React, { useState } from "react";
import styles from "./App.module.css";
import ExerciseCard from "./Exercisecard";
import NewExercise from "./Searchable";
import exercise from "./Exercise";
function App() {
    const [currExercise,addNewExercise]=useState([]);
    const [value, setValue] = useState("");
    
    function addNewExerciseCard(exerciseName){
        addNewExercise([exerciseName,...currExercise]);
    }
    return (
        <div>
            <NewExercise
                options={exercise}
                label="name"
                id="id"
                selectedVal={value}
                handleChange={(val) => setValue(val)}
                addNewExercise={addNewExerciseCard}
            />
           {currExercise.map((e, index) => {
    return <ExerciseCard key={index} exerciseName={e} />;
})}

            


        </div>
    );
}

export default App;
