import React, { useState } from "react";
import styles from "./App.module.css";

function App() {
    const [newSet, setNewSet] = useState(false);
    const [newSetDetails, setNewSetDetails] = useState({ reps: "", weight: "" });
    const [exercise, setExercise] = useState({
        name: "chest press",
        sets: [
            { sno: "1", reps: "12", weight: "15kg" },
            { sno: "2", reps: "10", weight: "20kg" },
            { sno: "3", reps: "8", weight: "25kg" },
        ],
    });

    // Show the form to add a new set
    function handleOnClick() {
        setNewSet(true);
    }

    // Handle input changes dynamically
    function handleOnChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        setNewSetDetails({ ...newSetDetails, [name]: value });
    }

    // Add a new set to the exercise
    function handleNewSet(event) {
        event.preventDefault(); // Prevent default form submission behavior

        const nextSno = (exercise.sets.length + 1).toString(); // Calculate the next serial number
        setExercise({
            ...exercise,
            sets: [
                ...exercise.sets,
                { sno: nextSno, reps: newSetDetails.reps, weight: newSetDetails.weight },
            ],
        });

        // Reset form and close it
        setNewSetDetails({ reps: "", weight: "" });
        setNewSet(false);
    }

    return (
        <div className={styles.appContainer}>
            <div>
                <div key={exercise.name}>
                    <h2>{exercise.name}</h2>
                    <ul>
                        {exercise.sets.map((set) => (
                            <li key={set.sno}>
                                Set {set.sno}: {set.reps} reps, {set.weight}
                            </li>
                        ))}
                    </ul>
                    {newSet && (
                        <form onSubmit={handleNewSet}>
                            <label htmlFor="reps">
                                Reps:
                                <input
                                    id="reps"
                                    name="reps"
                                    onChange={handleOnChange}
                                    value={newSetDetails.reps}
                                    type="text"
                                    placeholder="Enter reps"
                                />
                            </label>
                            <label htmlFor="weight">
                                Weight:
                                <input
                                    id="weight"
                                    name="weight"
                                    onChange={handleOnChange}
                                    value={newSetDetails.weight}
                                    type="text"
                                    placeholder="Enter weight"
                                />
                            </label>
                            <button type="submit">Add Set</button>
                        </form>
                    )}
                    {!newSet && <button onClick={handleOnClick}>Add New Set</button>}
                </div>
            </div>
        </div>
    );
}

export default App;
