import React, { useState } from "react";
import styles from "./App.module.css";

function App() {
    const [newSet, setNewSet] = useState(false)
    const [newSetDetails, setNewSetDetails] = useState({ reps: "", weight: "" });
    const [exercise, setExercise] = useState(
        {
            name: "chest press",
            sets: [
                { sno: "1", reps: "12", weight: "15kg" },
                { sno: "2", reps: "10", weight: "20kg" },
                { sno: "3", reps: "8", weight: "25kg" },
            ],
        },
    );
    function handleOnClick() {
        setNewSet(true);
    }
    function handleOnChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        setNewSetDetails({ ...newSetDetails, [name]: value });

    }
    function handleNewSet(event) {
        setExercise(() => {
            return {...exercise,sets:[...exercise.sets, {sno:exercise.sets.length+1,reps:newSetDetails.reps,weight:newSetDetails.weight}]};
        });
        setNewSetDetails({reps:"",weight:""});
        
        event.preventDefault();
    }
    return (
        <div className={styles.appContainer}>
            <div>
                <div key={exercise.name}>
                    <h2>{exercise.name}</h2>
                    <ul>
                        {exercise.sets.map((set) => (
                            <li key={set.sno}>
                                Set {set.sno}: {set.reps} reps, {set.weight}
                            </li>
                        ))}
                    </ul>
                    {newSet && <form onSubmit={handleNewSet}>
                        <label>reps:
                            <input name="reps"
                                onChange={handleOnChange} value={newSetDetails.reps} type="text" />
                        </label>
                        <label>weight:
                            <input
                                name="weight"
                                onChange={handleOnChange}
                                value={newSetDetails.weight}
                                type="text" />
                        </label>
                        <button type="submit">+</button>
                    </form>}
                    <button onClick={handleOnClick}>set</button>
                </div>
            </div>
        </div>
    );
}

export default App;
