import React, { useState, createContext } from "react";
import { Outlet } from "react-router-dom";

export const ExerciseContext = createContext();

function App() {
    const [currExercises, handleExercises] = useState({
        "Chest Press": [{ sno: "1", reps: "10", weight: "10" }],
    });
    const [value, setValue] = useState("");

    function addNewSet(newSetDetails, exerciseName) {
        handleExercises((prev) => ({
            ...prev,
            [exerciseName]: [
                ...(prev[exerciseName]),
                {
                    sno: prev[exerciseName].length + 1,
                    reps: newSetDetails.reps,
                    weight: newSetDetails.weight,
                },
            ],
        }));
    }

    function addNewExerciseCard(exerciseName) {
        handleExercises((prev) => ({
            ...prev,
            [exerciseName]: [],
        }));
    }

    const contextValue = {
        currExercises,
        value,
        setValue,
        addNewSet,
        addNewExerciseCard,
    };

    return (
        <ExerciseContext.Provider value={contextValue}>
            <div>
                <h1>We will never show this page</h1>
                <Outlet />
            </div>
        </ExerciseContext.Provider>
    );
}

export default App;
