import React, { useState } from "react";
import styles from "./App.module.css";
import ExerciseCard from "./Exercisecard";
import NewExercise from "./Searchable";
import exerciseList from "./Exercise";
function App() {
    const [currExercises, handleExercises] = useState({"Chest Press":{sno:"1",reps:"10",weight:"10"}});
    const [value, setValue] = useState("");
    function addNewSet(newSetDetails, exerciseName) {
        handleExercises((prev) => {
            return { ...prev, [exerciseName]: [...prev.exerciseName, { sno: prev.exerciseName.length + 1, reps: newSetDetails.reps, weight: newSetDetails.weight }] }
        }
        )
    };
    // function addNewExerciseCard(exerciseName) {
    //     addNewExercise([exerciseName, ...currExercise]);
    // }
    return (
        <div>





            {/* <NewExercise
                options={exercise}
                label="name"
                id="id"
                selectedVal={value}
                handleChange={(val) => setValue(val)}
                addNewExercise={addNewExerciseCard}
            /> */}
            {Object.keys(currExercises).map((exerciseName, index) => {
                return <ExerciseCard
                            key={index} 
                            exerciseName={exerciseName} 
                            addNewSet={addNewSet}
                        />;
            })}




        </div>
    );
}

export default App;
