import { useEffect, useRef, useState } from "react";
import styles from "./Searchable.module.css";

const SearchableDropdown = ({
  options,
  label,
  id,
  selectedVal,
  handleChange
}) => {
  const [query, setQuery] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const inputRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (inputRef.current && !inputRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  const selectOption = (option) => {
    setQuery('');
    handleChange(option[label]);
    setIsOpen(false); // Close dropdown after selecting an option
  };

  const toggle = (e) => {
    setIsOpen(e && e.target === inputRef.current);
  };

  const getDisplayValue = () => {
    if (query) return query;
    if (selectedVal) return selectedVal;
    return "";
  };

  const filter = (options) => {
    return options.filter(
      (option) => option[label].toLowerCase().includes(query.toLowerCase())
    );
  };

  return (
    <div className={styles.dropdown}>
      <div className={styles.control}>
        <div className={styles.selectedValue}>
          <input
            ref={inputRef}
            type="text"
            value={getDisplayValue()}
            name="searchTerm"
            onChange={(e) => {
              setQuery(e.target.value);
              handleChange(null);
            }}
            onClick={toggle}
          />
        </div>
        <div className={`${styles.arrow} ${isOpen ? styles.open : ""}`} />
      </div>

      <div className={`${styles.options} ${isOpen ? styles.open : ""}`}>
        {filter(options).map((option, index) => (
          <div
            onClick={() => selectOption(option)}
            className={`${styles.option} ${
              option[label] === selectedVal ? styles.selected : ""
            }`}
            key={`${id}-${index}`}
          >
            {option[label]}
          </div>
        ))}
      </div>
    </div>
  );
};


function NewExercise(props){
  const [query,setQuery]=useEffect("");
  const [isOpen,setIsOpen]=useEffect(false);

  function getDisplayValue(){
    if(query) return query;
    return props.selectedVal;
  }
  return (
    <div>
      <div>
        
      </div>
    </div>
  );
}
export {NewExercise};
export default SearchableDropdown;
